import React from 'react';

const RouteCard = ({ route }) => {
    const getDifficultyClass = (difficulty) => {
        switch (difficulty?.toLowerCase()) {
            case 'kolay': return 'bg-green-500 text-white';
            case 'orta': return 'bg-yellow-500 text-white';
            case 'zor': return 'bg-red-500 text-white';
            default: return 'bg-gray-500 text-white';
        }
    };

    return (
        // Kartın ana çerçevesi
        <div className="bg-white rounded-2xl shadow-md overflow-hidden border border-gray-200 hover:shadow-xl transition-shadow">
            {/* Resim Alanı */}
            <div className="relative">
                {/* Resmin kendisi yerine gri bir alan kullanıyoruz (ekran görüntüsündeki gibi) */}
                <div className="w-full h-48 bg-gray-200 flex items-center justify-center">
                    <span className="text-gray-400">{route.title}</span>
                </div>
                {/* <img src={route.imageUrl} alt={route.title} className="w-full h-48 object-cover" /> */}

                {/* Zorluk Rozeti */}
                <div className="absolute top-3 left-3">
                    <span className={`px-3 py-1 text-xs font-semibold rounded-full ${getDifficultyClass(route.difficulty)}`}>
                        {route.difficulty}
                    </span>
                </div>
            </div>

            {/* İçerik Alanı */}
            <div className="p-5">
                <h3 className="text-2xl font-bold text-gray-800 mb-3">{route.title}</h3>

                {/* İstatistikler */}
                <div className="flex items-center space-x-4 text-sm text-gray-600 mb-4">
                    <span>POI</span>
                    <span className="font-semibold">{route.rating}</span>
                </div>

                {/* Etiketler */}
                <div className="flex flex-wrap gap-2 mb-5">
                    {(route.tags || []).map(tag => (
                        <span key={tag} className="px-4 py-1.5 text-sm rounded-lg bg-gray-100 text-gray-700">{tag}</span>
                    ))}
                </div>

                {/* Ayırıcı Çizgi */}
                <div className="border-t border-gray-200 my-4"></div>

                {/* Alt Kısım: Yazar ve Buton */}
                <div className="flex justify-between items-center">
                    <span className="text-sm font-medium text-gray-800">{route.author?.name}</span>
                    <button className="px-5 py-2 font-semibold text-sm rounded-lg text-white bg-green-500 hover:bg-green-600">
                        İncele
                    </button>
                </div>

                {/* Yorum ve Beğeni Sayısı */}
                <div className="flex items-center text-sm text-gray-500 mt-3">
                    <span>{route.likes}</span>
                    <span className="mx-2">•</span>
                    <span>{route.comments} yorum</span>
                </div>
            </div>
        </div>
    );
};

export default RouteCard;
