import React, { useState } from 'react';
import FilterSidebar from '../components/Route/FilterSidebar';
import RouteCard from '../components/Route/RouteCard';

// Bu, uygulamanızdaki tüm rotaların ana listesidir.
const allRoutes = [
    { id: 1, title: 'İstanbul Tarihi Yarımada Turu', difficulty: 'Kolay', rating: 4.8, distance: 8, durationCategory: 'CokGunluk', category: 'Tarih', imageUrl: 'https://images.unsplash.com/photo-1541561085361-3454c67b9b1e?ixlib=rb-4.0.3&q=85&fm=jpg&crop=entropy&cs=srgb&w=1600', tags: ['Tarih', 'Kültür'], author: { name: 'Ahmet Yılmaz' }, likes: 124, comments: 203 },
    { id: 2, title: 'Kapadokya Macera Rotası', difficulty: 'Orta', rating: 4.9, distance: 25, durationCategory: 'CokGunluk', category: 'Doğa', imageUrl: 'https://images.unsplash.com/photo-1579882246019-3919d3f55e46?ixlib=rb-4.0.3&q=85&fm=jpg&crop=entropy&cs=srgb&w=1600', tags: ['Doğa', 'Macera'], author: { name: 'Elif Kaya' }, likes: 250, comments: 150 },
    { id: 3, title: 'Ege Antik Kentler Turu', difficulty: 'Zor', rating: 4.7, distance: 45, durationCategory: 'CokGunluk', category: 'Tarih', imageUrl: 'https://images.unsplash.com/photo-1627895439213-3663a196e855?ixlib=rb-4.0.3&q=85&fm=jpg&crop=entropy&cs=srgb&w=1600', tags: ['Arkeoloji'], author: { name: 'Mehmet Özkan' }, likes: 45, comments: 87 },
    { id: 4, title: 'Likya Yolu Yürüyüşü', difficulty: 'Zor', rating: 4.9, distance: 50, durationCategory: 'CokGunluk', category: 'Doğa', imageUrl: 'https://images.unsplash.com/photo-1589994248590-9333a25c6433?ixlib=rb-4.0.3&q=85&fm=jpg&crop=entropy&cs=srgb&w=1600', tags: ['Trekking'], author: { name: 'Ayşe Vural' }, likes: 500, comments: 312 },
    { id: 5, title: 'Ankara Gurme Turu', difficulty: 'Kolay', rating: 4.5, distance: 5, durationCategory: 'Orta', category: 'Yemek', imageUrl: 'https://images.unsplash.com/photo-1620025743469-9524997ed3a9?ixlib=rb-4.0.3&q=85&fm=jpg&crop=entropy&cs=srgb&w=1600', tags: ['Yemek'], author: { name: 'Can Tekin' }, likes: 95, comments: 40 }
];

const RoutesPage = () => {
    // Tüm filtrelerin değerlerini tek bir state'te tutuyoruz.
    const [filters, setFilters] = useState({
        sortOption: null,
        selectedCategory: null,
        ratingValue: 1.0,
        selectedDuration: null,
        selectedDifficulty: null,
        distanceValue: 50
    });
    const [filteredRoutes, setFilteredRoutes] = useState(allRoutes);

    // Sidebar'daki herhangi bir değişiklik olduğunda bu fonksiyon çalışır ve state'i günceller.
    const handleFilterChange = (filterName, value) => {
        setFilters(prevFilters => ({ ...prevFilters, [filterName]: value }));
    };

    // "Filtreleri Uygula" butonuna basıldığında bu fonksiyon çalışır.
    const handleApplyFilters = () => {
        let tempRoutes = [...allRoutes];
        tempRoutes = tempRoutes.filter(route => 
            route.rating >= filters.ratingValue &&
            route.distance <= filters.distanceValue &&
            (!filters.selectedCategory || route.category === filters.selectedCategory) &&
            (!filters.selectedDuration || route.durationCategory === filters.selectedDuration) &&
            (!filters.selectedDifficulty || route.difficulty === filters.selectedDifficulty)
        );
        setFilteredRoutes(tempRoutes);
    };

    // Filtreleri ve listeyi başlangıç haline döndürür.
    const handleResetFilters = () => {
        setFilters({
            sortOption: null, selectedCategory: null, ratingValue: 1.0,
            selectedDuration: null, selectedDifficulty: null, distanceValue: 50
        });
        setFilteredRoutes(allRoutes);
    };

    return (
        <div className="bg-gray-50 min-h-screen">
            <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-8">
                <main className="grid grid-cols-1 lg:grid-cols-4 gap-8">
                    {/* State ve fonksiyonlar, props olarak Sidebar'a gönderilir. */}
                    <FilterSidebar
                        filters={filters}
                        onFilterChange={handleFilterChange}
                        onApply={handleApplyFilters}
                        onReset={handleResetFilters}
                    />
                    <div className="lg:col-span-3">
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                            {filteredRoutes.length > 0 ? (
                                filteredRoutes.map(route => <RouteCard key={route.id} route={route} />)
                            ) : (
                                <p className="col-span-2 text-center text-gray-500 py-10">Bu kriterlere uygun rota bulunamadı.</p>
                            )}
                        </div>
                    </div>
                </main>
            </div>
        </div>
    );
};

export default RoutesPage;
