import React, { useState, useEffect } from 'react';
import FilterSidebar from '../components/Route/FilterSidebar';
import RouteCard from '../components/Route/RouteCard';

// Filtrelemeyi kolaylaştırmak için mock verisine 'distance' gibi ek alanlar ekleyelim
const mockRoutes = [
    {
        id: 1, title: 'İstanbul Tarihi Yarımada Turu', description: 'Sultanahmet, Ayasofya, Topkapı...',
        imageUrl: 'https://images.unsplash.com/photo-1541561085361-3454c67b9b1e?ixlib=rb-4.0.3&q=85&fm=jpg&crop=entropy&cs=srgb&w=1600',
        difficulty: 'Kolay', duration: '2 gün', poiCount: 12, rating: 4.8, distance: 8, isPrivate: false,
        tags: ['Tarih', 'Kültür', 'Yürüyüş'],
        author: { name: 'Ahmet Yılmaz', avatarUrl: 'https://randomuser.me/api/portraits/men/32.jpg' },
        likes: 124, comments: 203
    },
    {
        id: 2, title: 'Kapadokya Macera Rotası', description: 'Balon turu, yeraltı şehirleri, trekking...',
        imageUrl: 'https://images.unsplash.com/photo-1579882246019-3919d3f55e46?ixlib=rb-4.0.3&q=85&fm=jpg&crop=entropy&cs=srgb&w=1600',
        difficulty: 'Orta', duration: '3 gün', poiCount: 8, rating: 4.9, distance: 25, isPrivate: false,
        tags: ['Doğa', 'Macera', 'Fotoğraf'],
        author: { name: 'Elif Kaya', avatarUrl: 'https://randomuser.me/api/portraits/women/44.jpg' },
        likes: 250, comments: 150
    },
    {
        id: 3, title: 'Ege Antik Kentler Turu', description: 'Efes, Bergama, Sardis antik kentleri...',
        imageUrl: 'https://images.unsplash.com/photo-1627895439213-3663a196e855?ixlib=rb-4.0.3&q=85&fm=jpg&crop=entropy&cs=srgb&w=1600',
        difficulty: 'Zor', duration: '4 gün', poiCount: 15, rating: 4.7, distance: 45, isPrivate: true,
        tags: ['Arkeoloji', 'Tarih', 'Müze'],
        author: { name: 'Mehmet Özkan', avatarUrl: 'https://randomuser.me/api/portraits/men/36.jpg' },
        likes: 45, comments: 87
    },
     {
        id: 4, title: 'Likya Yolu Yürüyüşü', description: 'Fethiye\'den Antalya\'ya uzanan tarihi yol...',
        imageUrl: 'https://images.unsplash.com/photo-1589994248590-9333a25c6433?ixlib=rb-4.0.3&q=85&fm=jpg&crop=entropy&cs=srgb&w=1600',
        difficulty: 'Zor', duration: '7+ gün', poiCount: 30, rating: 4.9, distance: 50, isPrivate: false,
        tags: ['Doğa', 'Trekking', 'Macera'],
        author: { name: 'Ayşe Vural', avatarUrl: 'https://randomuser.me/api/portraits/women/50.jpg' },
        likes: 500, comments: 312
    },
];


const RoutesPage = () => {
    // 1. FİLTRE DURUMLARI (STATE'LER) BURAYA TAŞINDI
    const [ratingValue, setRatingValue] = useState(1.0);
    const [distanceValue, setDistanceValue] = useState(50);
    const [selectedDifficulty, setSelectedDifficulty] = useState(null);
    // Diğer filtre state'lerini de buraya ekleyebilirsiniz (kategori, süre vb.)

    // 2. FİLTRELENMİŞ ROTALARI GÖSTERMEK İÇİN YENİ BİR STATE
    const [filteredRoutes, setFilteredRoutes] = useState(mockRoutes);

    // 3. FİLTRELERİ UYGULAMA FONKSİYONU
    const handleApplyFilters = () => {
        let tempRoutes = [...mockRoutes];

        // Puan filtresi
        tempRoutes = tempRoutes.filter(route => route.rating >= ratingValue);

        // Mesafe filtresi
        tempRoutes = tempRoutes.filter(route => route.distance <= distanceValue);

        // Zorluk filtresi
        if (selectedDifficulty) {
            tempRoutes = tempRoutes.filter(route => route.difficulty === selectedDifficulty);
        }

        // Diğer filtreleri de buraya ekleyebilirsiniz...

        setFilteredRoutes(tempRoutes);
    };

    // 4. FİLTRELERİ SIFIRLAMA FONKSİYONU
    const handleResetFilters = () => {
        setRatingValue(1.0);
        setDistanceValue(50);
        setSelectedDifficulty(null);
        setFilteredRoutes(mockRoutes); // Listeyi orijinal haline döndür
    };

    const [activeTab, setActiveTab] = useState('Tümü');
    const tabs = ['Tümü', 'Popüler', 'Yeni', 'En Çok Beğenilen'];

    return (
        <div className="bg-gray-50 min-h-screen">
            <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-8">
                {/* ... (Sayfa başlığı ve sekmeler - Değişiklik yok) ... */}
                <div className="flex justify-between items-center mb-6"><div><h1 className="text-4xl font-bold text-gray-900">Rotalar</h1><p className="mt-1 text-lg text-gray-600">Kullanıcılar tarafından oluşturulan rotaları keşfedin ve kendi rotalarınızı paylaşın</p></div><button className="hidden sm:flex items-center px-5 py-3 font-semibold text-white rounded-xl bg-gradient-to-r from-green-400 to-orange-400 hover:from-green-500 hover:to-orange-500 shadow-md"><i className="pi pi-plus mr-2 font-bold"></i>Yeni Rota Oluştur</button></div>
                <div className="bg-white p-2 rounded-2xl shadow-sm flex justify-between items-center mb-8"><div className="flex space-x-1">{tabs.map(tab => (<button key={tab} onClick={() => setActiveTab(tab)} className={`px-4 py-2 text-sm font-semibold rounded-lg transition-colors ${activeTab === tab ? 'bg-green-100 text-green-800' : 'text-gray-600 hover:bg-gray-100'}`}>{tab}</button>))}</div><div className="flex space-x-2"><button className="px-4 py-2 text-sm font-semibold text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-100 flex items-center"><i className="pi pi-filter mr-2"></i>Filtrele</button><button className="px-4 py-2 text-sm font-semibold text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-100 flex items-center"><i className="pi pi-sort-alt mr-2"></i>Sırala</button></div></div>

                <main className="grid grid-cols-1 lg:grid-cols-4 gap-8">
                    {/* 5. STATE'LER VE FONKSİYONLAR PROPS OLARAK SIDEBAR'A GÖNDERİLİYOR */}
                    <FilterSidebar
                        ratingValue={ratingValue}
                        onRatingChange={setRatingValue}
                        distanceValue={distanceValue}
                        onDistanceChange={setDistanceValue}
                        selectedDifficulty={selectedDifficulty}
                        onDifficultyChange={setSelectedDifficulty}
                        onApply={handleApplyFilters}
                        onReset={handleResetFilters}
                    />

                    <div className="lg:col-span-3">
                        {/* 6. FİLTRELENMİŞ ROTALAR EKRANA ÇİZİLİYOR */}
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                            {filteredRoutes.length > 0 ? (
                                filteredRoutes.map(route => (
                                    <RouteCard key={route.id} route={route} />
                                ))
                            ) : (
                                <p className="col-span-2 text-center text-gray-500">Bu kriterlere uygun rota bulunamadı.</p>
                            )}
                        </div>

                        <div className="text-center mt-10">
                            <button className="px-6 py-3 font-semibold text-gray-700 bg-white border-2 border-gray-300 rounded-xl hover:bg-gray-100 transition-colors flex items-center mx-auto">
                                <i className="pi pi-refresh mr-2"></i> Daha Fazla Rota Yükle
                            </button>
                        </div>
                    </div>
                </main>
            </div>
        </div>
    );
};

export default RoutesPage;
