import React from 'react';
import { Dropdown } from 'primereact/dropdown';
import { RadioButton } from 'primereact/radiobutton';
import { Slider } from 'primereact/slider';
import RatingFilter from './RatingFilter';

// 1. 'onFilterChange' prop'u RoutesPage'den alınır.
const FilterSidebar = ({ filters, onFilterChange, onApply, onReset }) => {

    const sortOptions = [ { name: 'Popülerliğe Göre', code: 'POP' }, /* ... */ ];
    const categories = [ { name: 'Tarih', key: 'Tarih', count: 45 }, /* ... */ ];
    // ... diğer veri dizileri

    return (
        <aside className="lg:col-span-1 flex flex-col space-y-6">

            {/* Sıralama ve Kategori gibi diğer filtreler */}
            <div className="bg-white p-6 rounded-2xl shadow-sm">
                <h3 className="text-lg font-semibold text-gray-800 flex items-center mb-4"><i className="pi pi-sort-alt mr-2"></i>Sıralama</h3>
                <Dropdown value={filters.sortOption} onChange={(e) => onFilterChange('sortOption', e.value)} options={sortOptions} optionLabel="name" placeholder="Sıralama türü seçin" className="w-full" />
            </div>
            {/* ... diğer filtre kartları ... */}


            {/* 2. BAĞLANTI KURULUYOR:
                'onFilterChange' fonksiyonu, 'RatingFilter' bileşenine 'onChange' prop'u olarak aktarılır.
                Artık RatingFilter içindeki slider hareket ettiğinde, RoutesPage'deki state güncellenecektir.
            */}
            <RatingFilter 
                value={filters.ratingValue} 
                onChange={(value) => onFilterChange('ratingValue', value)}
            />

            {/* ... kalan diğer filtre kartları ... */}
            <div className="bg-white p-6 rounded-2xl shadow-sm">
                 <h3 className="text-lg font-semibold text-gray-800 flex items-center mb-4"><i className="pi pi-map-marker mr-2"></i>Mesafe (km)</h3>
                 <p className="text-sm text-gray-600 mb-3">Maksimum Mesafe: {filters.distanceValue} km</p>
                 <Slider value={filters.distanceValue} onChange={(e) => onFilterChange('distanceValue', e.value)} min={1} max={50} className="w-full" />
                 <div className="flex justify-between text-xs text-gray-500 mt-1"><span>1 km</span><span>50+ km</span></div>
            </div>

            {/* Butonlar */}
            <div className="space-y-3">
                <button onClick={onApply} className="w-full ...">Filtreleri Uygula</button>
                <button onClick={onReset} className="w-full ...">Filtreleri Sıfırla</button>
            </div>
        </aside>
    );
};

export default FilterSidebar;
