import React from 'react';
// Dropdown'a artık ihtiyacımız yok
import { RadioButton } from 'primereact/radiobutton';
import { Slider } from 'primereact/slider';
import RatingFilter from './RatingFilter';

// Bileşen artık state'leri ve fonksiyonları props olarak alıyor
const FilterSidebar = ({ filters, onFilterChange, onReset }) => {

    // Sıralama seçenekleri RadioButton için düzenlendi
    const sortOptions = [
        { name: 'Popülerliğe Göre', key: 'POP' },
        { name: 'Yeniye Göre', key: 'NEW' },
        { name: 'Puana Göre', key: 'RATE' },
    ];

    const categories = [
        { name: 'Tarih', key: 'Tarih', count: 45 },
        { name: 'Sanat', key: 'Sanat', count: 32 },
        { name: 'Yemek', key: 'Yemek', count: 28 },
        { name: 'Doğa', key: 'Doğa', count: 36 },
    ];

    const durations = [
        { name: 'Kısa (2-4 saat)', key: 'Kisa', count: 23 },
        { name: 'Orta (4-6 saat)', key: 'Orta', count: 31 },
        { name: 'Uzun (6+ saat)', key: 'Uzun', count: 18 },
        { name: 'Çok Günlük', key: 'CokGunluk', count: 12 },
    ];

    const difficulties = [
        { name: 'Kolay', key: 'Kolay', count: 42, color: 'bg-green-500' },
        { name: 'Orta', key: 'Orta', count: 28, color: 'bg-yellow-500' },
        { name: 'Zor', key: 'Zor', count: 14, color: 'bg-red-500' },
    ];

    return (
        <aside className="lg:col-span-1 flex flex-col space-y-6">

            {/* SIRALAMA KARTI DROPDOWN YERİNE RADIOBUTTON KULLANACAK ŞEKİLDE GÜNCELLENDİ */}
            <div className="bg-white p-6 rounded-2xl shadow-sm">
                <h3 className="text-lg font-semibold text-gray-800 flex items-center mb-4">
                    <i className="pi pi-sort-alt mr-2"></i>Sıralama
                </h3>
                <div className="space-y-3">
                    {sortOptions.map((option) => (
                        <div key={option.key} className="flex items-center">
                            <RadioButton 
                                inputId={option.key} 
                                name="sortOption" 
                                value={option.key} 
                                onChange={(e) => onFilterChange('sortOption', e.value)} 
                                checked={filters.sortOption === option.key} 
                            />
                            <label htmlFor={option.key} className="ml-2 text-gray-700 cursor-pointer">
                                {option.name}
                            </label>
                        </div>
                    ))}
                </div>
            </div>

            {/* Kategori Filtreleri Kartı */}
            <div className="bg-white p-6 rounded-2xl shadow-sm">
                <h3 className="text-lg font-semibold text-gray-800 flex items-center mb-4"><i className="pi pi-filter mr-2"></i>Kategori Filtreleri</h3>
                <div className="space-y-4">{categories.map((cat) => (<div key={cat.key} className="flex items-center justify-between"><div className="flex items-center"><RadioButton inputId={cat.key} name="category" value={cat.name} onChange={(e) => onFilterChange('selectedCategory', e.value)} checked={filters.selectedCategory === cat.name} /><label htmlFor={cat.key} className="ml-2 text-gray-700">{cat.name}</label></div><span className="text-sm bg-gray-100 text-gray-600 font-medium px-2 py-0.5 rounded-full">{cat.count}</span></div>))}</div>
            </div>

            {/* Yeni Puan Aralığı Bileşeni */}
            <RatingFilter 
                value={filters.ratingValue} 
                onChange={(value) => onFilterChange('ratingValue', value)}
            />

            {/* Diğer Filtreler... (Bunlarda değişiklik yok) */}
            <div className="bg-white p-6 rounded-2xl shadow-sm"><h3 className="text-lg font-semibold text-gray-800 flex items-center mb-4"><i className="pi pi-clock mr-2"></i>Süre Filtreleri</h3><div className="space-y-4">{durations.map((dur) => (<div key={dur.key} className="flex items-center justify-between"><div className="flex items-center"><RadioButton inputId={dur.key} name="duration" value={dur.key} onChange={(e) => onFilterChange('selectedDuration', e.value)} checked={filters.selectedDuration === dur.key} /><label htmlFor={dur.key} className="ml-2 text-gray-700">{dur.name}</label></div><span className="text-sm bg-gray-100 text-gray-600 font-medium px-2 py-0.5 rounded-full">{dur.count}</span></div>))}</div></div>
            <div className="bg-white p-6 rounded-2xl shadow-sm"><h3 className="text-lg font-semibold text-gray-800 flex items-center mb-4"><i className="pi pi-bolt mr-2"></i>Zorluk Seviyesi</h3><div className="space-y-4">{difficulties.map((diff) => (<div key={diff.key} className="flex items-center justify-between"><div className="flex items-center"><RadioButton inputId={diff.key} name="difficulty" value={diff.name} onChange={(e) => onFilterChange('selectedDifficulty', e.value)} checked={filters.selectedDifficulty === diff.name} /><label htmlFor={diff.key} className="ml-2 text-gray-700">{diff.name}</label></div><span className={`text-sm text-white font-medium px-2.5 py-0.5 rounded-full ${diff.color}`}>{diff.count}</span></div>))}</div></div>
            <div className="bg-white p-6 rounded-2xl shadow-sm"><h3 className="text-lg font-semibold text-gray-800 flex items-center mb-4"><i className="pi pi-map-marker mr-2"></i>Mesafe (km)</h3><p className="text-sm text-gray-600 mb-3">Maksimum Mesafe: {filters.distanceValue} km</p><Slider value={filters.distanceValue} onChange={(e) => onFilterChange('distanceValue', e.value)} min={1} max={50} className="w-full" /><div className="flex justify-between text-xs text-gray-500 mt-1"><span>1 km</span><span>50+ km</span></div></div>

            {/* Buton */}
            <div className="space-y-3">
                <button onClick={onReset} className="w-full flex justify-center items-center py-3 px-4 font-semibold text-gray-700 bg-white border border-gray-300 rounded-xl hover:bg-gray-100">
                    <i className="pi pi-refresh mr-2"></i>Tüm Filtreleri Sıfırla
                </button>
            </div>
        </aside>
    );
};

export default FilterSidebar;
