import React, { useState } from 'react';
import { Dropdown } from 'primereact/dropdown';
import { RadioButton } from 'primereact/radiobutton';
import { Slider } from 'primereact/slider'; // Slider bileşenini import ediyoruz

const FilterSidebar = () => {
    // Mevcut state'ler
    const [sortOption, setSortOption] = useState(null);
    const [selectedCategory, setSelectedCategory] = useState(null);

    // Yeni eklenen filtreler için state'ler
    const [ratingValue, setRatingValue] = useState(4.0);
    const [selectedDuration, setSelectedDuration] = useState(null);
    const [selectedDifficulty, setSelectedDifficulty] = useState(null);
    const [distanceValue, setDistanceValue] = useState(15);

    const sortOptions = [
        { name: 'Popülerliğe Göre', code: 'POP' },
        { name: 'Yeniye Göre', code: 'NEW' },
        { name: 'Puana Göre', code: 'RATE' },
    ];

    const categories = [
        { name: 'Tarih', key: 'Tarih', count: 45 },
        { name: 'Sanat', key: 'Sanat', count: 32 },
        { name: 'Yemek', key: 'Yemek', count: 28 },
        { name: 'Doğa', key: 'Doğa', count: 36 },
    ];

    // Yeni filtreler için veri listeleri
    const durations = [
        { name: 'Kısa (2-4 saat)', key: 'Kisa', count: 23 },
        { name: 'Orta (4-6 saat)', key: 'Orta', count: 31 },
        { name: 'Uzun (6+ saat)', key: 'Uzun', count: 18 },
        { name: 'Çok Günlük', key: 'CokGunluk', count: 12 },
    ];

    const difficulties = [
        { name: 'Kolay', key: 'Kolay', count: 42, color: 'bg-green-500' },
        { name: 'Orta', key: 'Orta', count: 28, color: 'bg-yellow-500' },
        { name: 'Zor', key: 'Zor', count: 14, color: 'bg-red-500' },
    ];

    return (
        // Tüm filtreleri ve butonları kapsayan ana element
        <aside className="lg:col-span-1 flex flex-col space-y-6">

            {/* Sıralama Kartı (Mevcut) */}
            <div className="bg-white p-6 rounded-2xl shadow-sm">
                <h3 className="text-lg font-semibold text-gray-800 flex items-center mb-4"><i className="pi pi-sort-alt mr-2"></i>Sıralama</h3>
                <Dropdown value={sortOption} onChange={(e) => setSortOption(e.value)} options={sortOptions} optionLabel="name" placeholder="Sıralama türü seçin" className="w-full" />
            </div>

            {/* Kategori Filtreleri Kartı (Mevcut) */}
            <div className="bg-white p-6 rounded-2xl shadow-sm">
                <h3 className="text-lg font-semibold text-gray-800 flex items-center mb-4"><i className="pi pi-filter mr-2"></i>Kategori Filtreleri</h3>
                <div className="space-y-4">{categories.map((category) => (<div key={category.key} className="flex items-center justify-between"><div className="flex items-center"><RadioButton inputId={category.key} name="category" value={category.name} onChange={(e) => setSelectedCategory(e.value)} checked={selectedCategory === category.name} /><label htmlFor={category.key} className="ml-2 text-gray-700">{category.name}</label></div><span className="text-sm bg-gray-100 text-gray-600 font-medium px-2 py-0.5 rounded-full">{category.count}</span></div>))}</div>
            </div>

            {/* --- YENİ EKLENEN FİLTRELER --- */}

            {/* Puan Aralığı Kartı */}
            <RatingFilter 
                value={ratingValue} 
                onChange={onRatingChange} 
            />
            {/* Süre Filtreleri Kartı */}
            <div className="bg-white p-6 rounded-2xl shadow-sm">
                <h3 className="text-lg font-semibold text-gray-800 flex items-center mb-4"><i className="pi pi-clock mr-2"></i>Süre Filtreleri</h3>
                <div className="space-y-4">{durations.map((duration) => (<div key={duration.key} className="flex items-center justify-between"><div className="flex items-center"><RadioButton inputId={duration.key} name="duration" value={duration.name} onChange={(e) => setSelectedDuration(e.value)} checked={selectedDuration === duration.name} /><label htmlFor={duration.key} className="ml-2 text-gray-700">{duration.name}</label></div><span className="text-sm bg-gray-100 text-gray-600 font-medium px-2 py-0.5 rounded-full">{duration.count}</span></div>))}</div>
            </div>

            {/* Zorluk Seviyesi Kartı */}
            <div className="bg-white p-6 rounded-2xl shadow-sm">
                <h3 className="text-lg font-semibold text-gray-800 flex items-center mb-4"><i className="pi pi-bolt mr-2"></i>Zorluk Seviyesi</h3>
                <div className="space-y-4">{difficulties.map((difficulty) => (<div key={difficulty.key} className="flex items-center justify-between"><div className="flex items-center"><RadioButton inputId={difficulty.key} name="difficulty" value={difficulty.name} onChange={(e) => setSelectedDifficulty(e.value)} checked={selectedDifficulty === difficulty.name} /><label htmlFor={difficulty.key} className="ml-2 text-gray-700">{difficulty.name}</label></div><span className={`text-sm text-white font-medium px-2.5 py-0.5 rounded-full ${difficulty.color}`}>{difficulty.count}</span></div>))}</div>
            </div>

            {/* Mesafe (km) Kartı */}
            <div className="bg-white p-6 rounded-2xl shadow-sm">
                <h3 className="text-lg font-semibold text-gray-800 flex items-center mb-4"><i className="pi pi-map-marker mr-2"></i>Mesafe (km)</h3>
                <p className="text-sm text-gray-600 mb-3">Maksimum Mesafe: {distanceValue} km</p>
                <Slider value={distanceValue} onChange={(e) => setDistanceValue(e.value)} min={1} max={50} className="w-full" />
                <div className="flex justify-between text-xs text-gray-500 mt-1"><span>1 km</span><span>50+ km</span></div>
            </div>

            {/* Butonlar */}
            <div className="space-y-3">
                <button className="w-full flex justify-center items-center py-3 px-4 font-semibold text-white rounded-xl bg-gradient-to-r from-cyan-500 to-orange-400 hover:opacity-90 shadow-md">
                    <i className="pi pi-check mr-2"></i>Filtreleri Uygula
                </button>
                <button className="w-full flex justify-center items-center py-3 px-4 font-semibold text-gray-700 bg-white border border-gray-300 rounded-xl hover:bg-gray-100">
                    <i className="pi pi-refresh mr-2"></i>Filtreleri Sıfırla
                </button>
            </div>
        </aside>
    );
};

export default FilterSidebar;
